let raw = $input.first().json.data || "";

// --------------------------------------------
// 1. Remove purely tracking/analytics URLs only
// --------------------------------------------
raw = raw.replace(/https?:\/\/(www\.)?linkedin\.com\/login\S*/gi, ""); 
raw = raw.replace(/https?:\/\/(www\.)?linkedin\.com\/search\S*/gi, "");

// --------------------------------------------
// 2. Remove markdown image syntax but keep links text
// --------------------------------------------
raw = raw.replace(/!\[[^\]]*?\]\([^)]*?\)/g, "");  // remove markdown images
raw = raw.replace(/\[([^\]]+)\]\([^)]+\)/g, "$1"); // keep link text, remove URL

// --------------------------------------------
// 3. Remove repetitive UI/navigation text
// --------------------------------------------
const uiNoise = [
  /Skip to main content/gi,
  /Expand search/gi,
  /Sign in.*/gi,
  /Join now.*/gi,
  /Save job.*/gi,
  /Report this job.*/gi,
  /Set alert/gi,
  /Welcome back/gi,
  /Continue with Google/gi,
  /Forgot password\?/gi,
  /By clicking Continue/gi,
  /People also viewed[\s\S]*/gi,   // remove sidebar suggestions
  /Similar jobs[\s\S]*/gi          // remove repeated job cards
];

uiNoise.forEach(rx => raw = raw.replace(rx, ""));

// --------------------------------------------
// 4. Normalize whitespace
// --------------------------------------------
raw = raw.replace(/\r/g, "");
raw = raw.replace(/[ \t]+/g, " ");
raw = raw.replace(/\n{3,}/g, "\n\n");

// --------------------------------------------
// 5. Trim and remove purely empty lines
// --------------------------------------------
raw = raw.split("\n").map(line => line.trim()).filter(line => line !== "").join("\n");

// Return cleaned but still readable job text
return { cleaned: raw };
